import React, { useState, useCallback } from 'react';
import Navbar from './components/Navbar';
import FilteringComponents from './components/FilteringComponents';
import CountriesList from './components/CountriesList';
import CountryDetails from './components/CountryDetails';
import CountriesProvider from './context/CountriesContext';
import { ThemeProvider } from './context/ThemeContext';
import './styles/CountriesList.module.scss';

function App() {
  const [selectedCountry, setSelectedCountry] = useState(null);

  // Stabilize the handleSelectCountry function
  const handleSelectCountry = useCallback((country) => {
    setSelectedCountry(country);
  }, []);

  const handleBackToList = () => {
    setSelectedCountry(null);
  };

  return (
    <div className="App">
      <ThemeProvider>
        <Navbar goBack={handleBackToList} />
        <CountriesProvider>
          {selectedCountry ? (
            <CountryDetails
              country={selectedCountry}
              goBack={handleBackToList}
              onSelectCountry={handleSelectCountry}
            />
          ) : (
            <>
              <FilteringComponents />
              <CountriesList onSelectCountry={handleSelectCountry} />
            </>
          )}
        </CountriesProvider>
      </ThemeProvider>
    </div>
  );
}

export default App;
