import React, { useState, useEffect } from 'react';
import styles from '../styles/CountryDetails.module.scss';

function CountryDetails({ country, goBack, onSelectCountry }) {
  const [borderCountryCode, setBorderCountryCode] = useState(null);

  useEffect(() => {
    if (borderCountryCode) {
      console.log(`Fetching data for border country code: ${borderCountryCode}`);
      fetch(`https://restcountries.com/v3.1/alpha/${borderCountryCode}`)
        .then((response) => response.json())
        .then((data) => {
          console.log('Data fetched for border country:', data);
          if (data && data.length > 0) {
            const countryData = data[0];
            onSelectCountry(countryData);
          } else {
            console.error('No data returned for this country code:', borderCountryCode);
          }
        })
        .catch((error) => {
          console.error('Error fetching border country:', error);
        });
    }
  }, [borderCountryCode, onSelectCountry]);

  return (
    <div className={styles.countryDetails}>
      <button onClick={goBack}>Back to List</button>
      <div className={styles.content}>
        <img src={country.flags.svg} alt={`${country.name.common} flag`} />
        <div className={styles.details}>
          {/* Other details */}
          <p>
            <strong>Borders:</strong>
            <span className={styles.flexCards}>
              {country.borders ? country.borders.map((border) =>
                <div key={border} className={styles.countryDiv} onClick={() => setBorderCountryCode(border)}>
                  {border}
                </div>
              ) : <p>No borders available.</p>}
            </span>
          </p>
        </div>
      </div>
    </div>
  );
}

export default CountryDetails;
