import React,{useState,useEffect} from 'react';
import styles from '../styles/CountryDetails.module.scss';

function CountryDetails({ country, goBack ,onSelectCountry}) {
  const [countries, setCountries] = useState([]);
    const [filteredCountries, setFilteredCountries] = useState([]);

    // useEffect((countryCode) => {
    //   fetch(`https://restcountries.com/v3.1/alpha/${countryCode}`)
    //     .then((response) => response.json())
    //     .then((data) => {
    //       setCountries(data);
    //       setFilteredCountries(data);
    //     })
    //     .catch((error) => console.error('Error fetching countries:', error));
    // }, []);
  return (
    <div className={styles.countryDetails}>
      <button onClick={goBack}>Back to List</button>
      <div className={styles.content}>
        <img src={country.flags.svg} alt={`${country.name.common} flag`} />
        <div className={styles.details}>
          <h2>{country.name.common}</h2>
          <p>
            <strong>Native Name: </strong>
            {
              Object.values(country.name.nativeName).map((name) => (
                <span key={name}>{name.common}, </span>
              ))
            }
          </p>
          <p>
            <strong>Population:</strong> {country.population.toLocaleString()}
          </p>
          <p>
            <strong>Region:</strong> {country.region}
          </p>
          <p>
            <strong>Sub Region:</strong> {country.subregion}
          </p>
          <p>
            <strong>Capital:</strong> {country.capital}
          </p>

          <p>
            <strong>Top Level Domain:</strong> {country.tld}
          </p>
          <p>
            <strong>Currencies:</strong> {country.currencies && Object.values(country.currencies).map((currency) => currency.name).join(', ')}
          </p>
          <p>
            <strong>Languages:</strong> {country.languages && Object.values(country.languages).join(', ')}
          </p>
          <p>
            borders: <span className={styles.flexCards}>{country.borders?country.borders.map((b) =><div className={styles.countrDiv} onClick={() => onSelectCountry(country)}>{b}</div>):<p>no </p>}
            {console.log(country.altSpellings)}</span>
          </p>

          <p>
            {console.log(country.name.nativeName)}
          </p>
        </div>
      </div>
    </div>
  );
}

export default CountryDetails;
